package Repositories;import Models.Author;import java.util.ArrayList;import java.util.List;public class AuthorCrudOperations implements CrudOperations<Author>{private List<Author> authors;public AuthorCrudOperations(){    this.authors = new ArrayList<>();}    @Override    public List<Author> findAll() {        return new ArrayList<>(authors);    }    @Override    public List<Author> saveAll(List<Author> toSave) {        authors.addAll(toSave);        return toSave;    }    @Override    public Author save(Author toSave) {        authors.add(toSave);        return toSave;    }    @Override    public Author delete(Author toDelete) {        authors.remove(toDelete);        return toDelete;    }}