package Repositories;import Models.Book;import java.util.ArrayList;import java.util.List;public class BookCrudOperations implements CrudOperations<Book> {    private List<Book> books;    public BookCrudOperations(){        this.books = new ArrayList<>();    }      @Override    public List<Book> findAll() {        return books;    }    @Override    public List<Book> saveAll(List<Book> toSave){        books.addAll(toSave);        return toSave;    }    @Override    public Book save(Book toSave){        books.add(toSave);        return toSave;    }    @Override    public Book delete (Book toDelete){        books.remove(toDelete);        return toDelete;    }}