package Settings;import java.sql.Connection;import java.sql.DriverManager;import java.sql.SQLException;public class DataBaseConnection {    private String url;    private String user;    private String password;    public static class ManagingDataBaseConnection {        private String url;        private String user;        private String password;        public ManagingDataBaseConnection() {            this.url = "jdbc:postgresql://localhost:5432/library_management?user=prog_admin";            this.user = System.getenv("user");            this.password = System.getenv("password");        }        public Connection getConnection() {            try {                Connection connection = DriverManager.getConnection(this.url, this.user, this.password);                if (connection != null && connection.isValid(2)) {                    System.out.println("Success ConnectionÂ§ ");                    return connection;                }            } catch (SQLException e) {                e.printStackTrace();            }            return null;        }    }}